//dashboardSlice.js
import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isPrivate: false,
  email: "",
  dateJoined: "",
  profile: {
    fullName: "",
    profilePicture: "",
    bio: "",
    roleTitle: "",
    location: "",
    phone: "",
    videoIntro: {
      platform: "",
    },
    achievements: [],
  },
  skillsAssessment: {
    quizzesTaken: [],
  },
  testimonials: [],
  analytics: {
    visitorDemographics: [],
  },
  repositories: [],
  resume: {
    education: [],
    workExperience: [],
    skills: [],
    projects: [],
    certifications: [],
    languages: [],
  },
};

export const dashboardSlice = createSlice({
  name: "dashboard",
  initialState,
  reducers: {
    // Reducer for setting the entire dashboard data
    setDashboardData: (state, action) => {
      return { ...state, ...action.payload };
    },
    setUser: (state, action) => {
      return { ...state, ...action.payload };
    },
    updateProfile: (state, action) => {
      state.profile = { ...state.profile, ...action.payload };
    },
    updateSkillsAssessment: (state, action) => {
      state.skillsAssessment = { ...state.skillsAssessment, ...action.payload };
    },
    updateTestimonials: (state, action) => {
      state.testimonials = [...action.payload]; // Assuming action.payload is an array of testimonials
    },
    updateRepositories: (state, action) => {
      state.repositories = [...action.payload]; // Assuming action.payload is an array of repositories
    },
    updateResume: (state, action) => {
      state.resume = { ...state.resume, ...action.payload };
    },
    // Reducer for updating a specific education entry
    updateEducationById: (state, action) => {
      const { _id, newData } = action.payload;
      const index = state.resume.education.findIndex(
        (item) => item._id === _id
      );
      if (index !== -1) {
        state.resume.education[index] = {
          ...state.resume.education[index],
          ...newData,
        };
      }
    },

    // Reducer for updating a specific work experience entry
    updateWorkExperienceById: (state, action) => {
      const { _id, newData } = action.payload;
      const index = state.resume.workExperience.findIndex(
        (item) => item._id === _id
      );
      if (index !== -1) {
        state.resume.workExperience[index] = {
          ...state.resume.workExperience[index],
          ...newData,
        };
      }
    },

    // Reducer for updating a specific skill
    updateSkillById: (state, action) => {
      const { _id, newData } = action.payload;
      const index = state.resume.skills.findIndex((skill) => skill._id === _id);
      if (index !== -1) {
        state.resume.skills[index] = {
          ...state.resume.skills[index],
          ...newData,
        };
      }
    },
    // Reducer for updating a specific project
    updateProjectById: (state, action) => {
      const { _id, newData } = action.payload;
      const index = state.resume.projects.findIndex(
        (project) => project._id === _id
      );
      if (index !== -1) {
        state.resume.projects[index] = {
          ...state.resume.projects[index],
          ...newData,
        };
      }
    },

    // Reducer for updating a specific certification
    updateCertificationById: (state, action) => {
      const { _id, newData } = action.payload;
      const index = state.resume.certifications.findIndex(
        (cert) => cert._id === _id
      );
      if (index !== -1) {
        state.resume.certifications[index] = {
          ...state.resume.certifications[index],
          ...newData,
        };
      }
    },

    // Reducer for updating a specific language
    updateLanguageById: (state, action) => {
      const { _id, newData } = action.payload;
      const index = state.resume.languages.findIndex(
        (lang) => lang._id === _id
      );
      if (index !== -1) {
        state.resume.languages[index] = {
          ...state.resume.languages[index],
          ...newData,
        };
      }
    },

    // Add other reducers as necessary

    // Reducer for removing a specific education entry
    removeEducationById: (state, action) => {
      const educationId = action.payload; // The ID of the education entry to remove
      state.resume.education = state.resume.education.filter(
        (education) => education._id !== educationId
      );
    },
  },
});

export const {
  setUser,
  updateProfile,
  updateSkillsAssessment,
  updateTestimonials,
  updateRepositories,
  updateResume,
  updateEducationById,
  updateWorkExperienceById,
  updateSkillById,
  updateProjectById,
  updateCertificationById,
  updateLanguageById,
  removeEducationById,
  setDashboardData,
} = dashboardSlice.actions;

export default dashboardSlice.reducer;
