import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  isPrivate: false,
  email: '',
  dateJoined: '',
  profile: {
    fullName: '',
    profilePicture: '',
    bio: '',
    roleTitle: '',
    location: '',
    phone: '',
    videoIntro: {
      platform: ''
    },
    achievements: []
  },
  skillsAssessment: {
    quizzesTaken: []
  },
  testimonials: [],
  analytics: {
    visitorDemographics: []
  },
  repositories: [],
  resume: {
    education: [],
    workExperience: [],
    skills: [],
    projects: [],
    certifications: [],
    languages: []
  }
};

export const dashboardSlice = createSlice({
  name: 'dashboard',
  initialState,
  reducers: {
    setUser: (state, action) => {
      return { ...state, ...action.payload };
    },
    // Add more reducers as needed for updating specific parts of the state
  },
});

export const { setUser } = dashboardSlice.actions;

export default dashboardSlice.reducer;
