import React, { useState } from "react";

const RegForm = () => {
  const [fullName, setFullName] = useState(""); // Add this line
  const [email, setEmail] = useState(""); // Add this line
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      setErrorMessage("Passwords do not match!");
      return;
    }

    try {
      // Send registration data to the backend
      const response = await fetch("http://localhost:5001/api/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          fullName,
          email,
          password,
        }),
      });

      const data = await response.json();

      if (data.error) {
        setErrorMessage(data.message);
      } else {
        // Handle success (maybe redirect to login or show a success message)
      }
    } catch (error) {
      console.error("Failed to register:", error);
      setErrorMessage("Something went wrong! Please try again later.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="p-4 p-md-5 border rounded-3 bg-light text-dark"
    >
      {errorMessage && (
        <div className="alert alert-danger" role="alert">
          {errorMessage}
        </div>
      )}
      {/* Full Name Field */}
      <div className="form-floating mb-3">
        <input
          type="text"
          className="form-control"
          id="floatingFullName"
          placeholder="John Doe"
          value={fullName} // Add this line
          onChange={(e) => setFullName(e.target.value)} // And this line
        />
        <label htmlFor="floatingFullName">Full Name</label>
      </div>

      {/* Email Field */}
      <div className="form-floating mb-3">
        <input
          type="email"
          className="form-control"
          id="floatingInput"
          placeholder="name@example.com"
          value={email} // Add this line
          onChange={(e) => setEmail(e.target.value)} // And this line
        />
        <label htmlFor="floatingInput">Email address</label>
      </div>

      {/* Password Field */}
      <div className="form-floating mb-3">
        <input
          type="password"
          className="form-control"
          id="floatingPassword"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <label htmlFor="floatingPassword">Password</label>
      </div>

      {/* Confirm Password Field */}
      <div className="form-floating mb-3">
        <input
          type="password"
          className="form-control"
          id="floatingConfirmPassword"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <label htmlFor="floatingConfirmPassword">Confirm Password</label>
      </div>

      {/* Remember Me Checkbox */}
      <div className="checkbox mb-3">
        <label>
          <input type="checkbox" value="remember-me" /> Remember me
        </label>
      </div>

      {/* Submit Button */}
      <button className="w-100 btn btn-lg btn-primary" type="submit">
        Sign Up for Free
      </button>
      <hr className="my-4" />
      <small className="text-muted">
        By clicking Sign up, you agree to the terms of use.
      </small>
    </form>
  );
};

export default RegForm;
