// PortfolioDataFetcher.js
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUserPortfolio } from '../../UserPortfolio/userPortfolioThunks.js';

const PortfolioDataFetcher = ({ children }) => {
  const dispatch = useDispatch();
  const userId = useSelector((state) => state.auth.userId);

  useEffect(() => {
    if (userId) {
      dispatch(fetchUserPortfolio(userId));
    }
  }, [userId, dispatch]);

  return children; // Render children components
};

export default PortfolioDataFetcher;
