import { createAsyncThunk } from '@reduxjs/toolkit';
import { fetchPortfolioData } from './services/portfolioService';

export const fetchUserPortfolio = createAsyncThunk(
  'userPortfolio/fetchUserPortfolio',
  async (token, thunkAPI) => {
    console.log('fetchUserPortfolio thunk called with token:', token); // Debugging line
    try {
      const response = await fetchPortfolioData(token); // Use the token to make the API call
      console.log('fetchUserPortfolio response:', response); // Debugging line
      return response; // Assuming the response is the data object itself
    } catch (error) {
      console.error('fetchUserPortfolio error:', error); // Debugging line
      return thunkAPI.rejectWithValue(error.response.data);
    }
  }
);

// Export your thunks as needed
export const userPortfolioThunks = {
  fetchUserPortfolio,
  // ... other thunks
};
