// userPortfolioSlice.js
import { createSlice } from '@reduxjs/toolkit';

export const userPortfolioSlice = createSlice({
  name: 'userPortfolio',
  initialState: {
    data: null,
    loading: false,
    error: null,
  },
  reducers: {
    fetchUserPortfolioStart: (state) => {
      state.loading = true;
    },
    fetchUserPortfolioSuccess: (state, action) => {
      state.data = action.payload;
      state.loading = false;
    },
    fetchUserPortfolioFailure: (state, action) => {
      state.error = action.payload;
      state.loading = false;
    },
  },
});

export const {
  fetchUserPortfolioStart,
  fetchUserPortfolioSuccess,
  fetchUserPortfolioFailure,
} = userPortfolioSlice.actions;

export default userPortfolioSlice.reducer;
