import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { useDispatch } from 'react-redux';
import { editEducation } from '../dashboardThunks'; // Import the editEducation thunk

// Validation Schema
const EducationSchema = Yup.object().shape({
  institution: Yup.string().required('Institution is required'),
  degree: Yup.string().required('Degree is required'),
  fieldOfStudy: Yup.string().required('Field of study is required'),
  startDate: Yup.date().required('Start date is required'),
  endDate: Yup.date(),
  description: Yup.string()
});

const EditEducationForm = ({ initialData, itemId }) => {
  const dispatch = useDispatch();

  const formik = useFormik({
    initialValues: {
      institution: initialData.institution || '',
      degree: initialData.degree || '',
      fieldOfStudy: initialData.fieldOfStudy || '',
      startDate: initialData.startDate || '',
      endDate: initialData.endDate || '',
      description: initialData.description || ''
    },
    validationSchema: EducationSchema,
    onSubmit: (values) => {
      // Dispatch the editEducation thunk with the item ID and new data
      dispatch(editEducation({ itemId, newData: values }));
    }
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      {/* Institution Input */}
      <input 
        type="text"
        name="institution"
        onChange={formik.handleChange}
        value={formik.values.institution}
        placeholder="Institution"
      />
      {formik.touched.institution && formik.errors.institution && (
        <div>{formik.errors.institution}</div>
      )}

      {/* Degree Input */}
      <input 
        type="text"
        name="degree"
        onChange={formik.handleChange}
        value={formik.values.degree}
        placeholder="Degree"
      />
      {formik.touched.degree && formik.errors.degree && (
        <div>{formik.errors.degree}</div>
      )}

      {/* Field of Study Input */}
      <input 
        type="text"
        name="fieldOfStudy"
        onChange={formik.handleChange}
        value={formik.values.fieldOfStudy}
        placeholder="Field of Study"
      />
      {formik.touched.fieldOfStudy && formik.errors.fieldOfStudy && (
        <div>{formik.errors.fieldOfStudy}</div>
      )}

      {/* Start Date Input */}
      <input 
        type="date"
        name="startDate"
        onChange={formik.handleChange}
        value={formik.values.startDate}
      />
      {formik.touched.startDate && formik.errors.startDate && (
        <div>{formik.errors.startDate}</div>
      )}

      {/* End Date Input */}
      <input 
        type="date"
        name="endDate"
        onChange={formik.handleChange}
        value={formik.values.endDate}
      />
      {formik.touched.endDate && formik.errors.endDate && (
        <div>{formik.errors.endDate}</div>
      )}

      {/* Description Input */}
      <textarea
        name="description"
        onChange={formik.handleChange}
        value={formik.values.description}
        placeholder="Description"
      />
      {formik.touched.description && formik.errors.description && (
        <div>{formik.errors.description}</div>
      )}

      {/* Submit Button */}
      <button type="submit">Save Changes</button>
    </form>
  );
};

export default EditEducationForm;
