import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteEducation } from "../dashboardThunks";
import { Modal } from "react-bootstrap";
import { useState } from "react";
import { EditEducationForm } from "./EditEducationForm";


const EducationList = () => {
  const dispatch = useDispatch();
  const education = useSelector((state) => state.dashboard.resume.education);

  const [showModal, setShowModal] = useState(false);
  const [selectedEducation, setSelectedEducation] = useState(null);

  useEffect(() => {
    console.log("Current education state:", education);
  }, [education]);

  const handleDelete = (id) => {
    dispatch(deleteEducation(id));
  };

  const handleEdit = (educationEntry) => {
    setSelectedEducation(educationEntry);
    setShowModal(true);
  };

  const handleAddNew = () => {
    setSelectedEducation(null);
    setShowModal(true);
  };

  if (!education.length) {
    return <div>No education records found.</div>;
  }

  return (
    <div><button onClick={handleAddNew}>Add New</button>

      {education.map((entry) => (
        <div key={entry._id}>
          <h3>{entry.institution}</h3>
          <p>{entry.degree}</p>
          {/* Additional fields */}
          <button onClick={() => handleEdit(entry)}>Edit</button>
          <button onClick={() => handleDelete(entry._id)}>Delete</button>
        </div>
      ))}

<Modal show={showModal} onHide={() => setShowModal(false)}>
  <Modal.Header closeButton>
    <Modal.Title>{selectedEducation ? 'Edit Education' : 'Add New Education'}</Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <EditEducationForm 
      initialData={selectedEducation || {}} 
      itemId={selectedEducation?._id} 
      closeModal={() => setShowModal(false)}
    />
  </Modal.Body>
</Modal>
    </div>
  );
};

export default EducationList;
