import React, { useState } from 'react';
import axios from 'axios';  // Only if you are using axios

const LoginForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [remember, setRemember] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault(); // Prevents the default form submission behaviour

        try {
            const response = await axios.post('http://localhost:5001/api/login', { // Assuming your express server is running on port 3001
                email,
                password
            });

            const { token } = response.data;

            // Save the token in localStorage or handle it as needed.
            localStorage.setItem('authToken', token);

            // Redirect to dashboard or wherever you wish
            // window.location.replace('/dashboard');

        } catch (error) {
            console.error('Error logging in:', error.response.data.message);
            // You might want to show an error message to the user here.
        }
    }

    return (
        <form onSubmit={handleSubmit} className="p-4 p-md-5 border rounded-3 bg-light text-dark">
            <div className="form-floating mb-3">
                <input 
                    type="email" 
                    className="form-control" 
                    id="loginFloatingInput" 
                    placeholder="name@example.com" 
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <label htmlFor="loginFloatingInput">Email address</label>
            </div>
            <div className="form-floating mb-3">
                <input 
                    type="password" 
                    className="form-control" 
                    id="loginFloatingPassword" 
                    placeholder="Password" 
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <label htmlFor="loginFloatingPassword">Password</label>
            </div>
            <div className="checkbox mb-3">
                <label>
                    <input 
                        type="checkbox" 
                        value="remember-me" 
                        checked={remember}
                        onChange={() => setRemember(prev => !prev)}
                    /> 
                    Remember me
                </label>
            </div>
            <button className="w-100 btn btn-lg btn-primary" type="submit">Login</button>
            <hr className="my-4" />
            <small className="text-muted">By logging in, you agree to our terms of use.</small>
        </form>
    );
}

export default LoginForm;
