import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import {
  updateEducationById,
  updateWorkExperienceById,
  updateSkillById,
  updateProjectById,
  updateCertificationById,
  updateLanguageById,
  removeEducationById,
  setDashboardData

  // ... other import actions ...
} from '../../reducers/dashboardSlice';

const API_URL = process.env.REACT_APP_API_URL;


// Example thunk for editing an education item
export const editEducation = createAsyncThunk(
  'dashboard/editEducation',
  async ({ itemId, newData }, { dispatch }) => {
    try {
        const response = await axios.put(`${API_URL}/dashboard/education/${itemId}`, newData);
      dispatch(updateEducationById({ _id: itemId, newData: response.data }));
      return response.data;
    } catch (error) {
      console.error('Error editing education:', error);
      // Handle errors as needed
    }
  }
);

// Similar thunks for other sections like workExperience, skills, etc.

// Thunk for deleting an education item
export const deleteEducation = createAsyncThunk(
    'dashboard/deleteEducation',
    async (educationId, { dispatch }) => {
      try {
        const response = await axios.delete(`${API_URL}/dashboard/education/${educationId}`);
        if (response.status === 200) {
          dispatch(removeEducationById(educationId));
        }
      } catch (error) {
        console.error('Error deleting education:', error);
        // Handle errors as needed
      }
    }
  );

// Thunk for fetching the user's portfolio
export const fetchUserPortfolio = createAsyncThunk(
    'dashboard/fetchUserPortfolio',
    async ({ identifier, token }, { dispatch }) => {
      try {
        const response = await axios.get(`${API_URL}/portfolio/${identifier}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        dispatch(setDashboardData(response.data));
        return response.data;
      } catch (error) {
        console.error('fetchUserPortfolio error:', error);
        // Handle errors as needed
      }
    }
  );

  // Similar thunks for other sections like workExperience, skills, etc.

export const userDashboardThunks = {

    editEducation,
    deleteEducation,
    fetchUserPortfolio,
    // ... other thunks ...
  };
