import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { authSlice } from './authSlice';

export const updateProfile = createAsyncThunk('profile/updateProfile', async (formData, thunkAPI) => {
  try {
    const response = await axios.put('http://localhost:5001/api/profile', formData, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
        'Content-Type': 'multipart/form-data',
      },
    });

    // Dispatch the updateProfile action from authSlice
    thunkAPI.dispatch(authSlice.actions.updateProfile(response.data));

    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.response.data);
  }
});
