// PortfolioDataFetcher.js
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUserPortfolio } from '../../UserPortfolio/userPortfolioThunks.js';

const PortfolioDataFetcher = () => {
  const dispatch = useDispatch();
  const userId = useSelector((state) => state.auth.userId);
  const token = useSelector((state) => state.auth.token); // Retrieve the token from auth state

  useEffect(() => {
    if (userId && token) {
      dispatch(fetchUserPortfolio({ identifier: userId, token }));
    }
  }, [userId, token, dispatch]);

  return null; // Component does not render anything
};

export default PortfolioDataFetcher;
