import React from 'react';
import { useSelector } from 'react-redux';

const UserInfo = () => {
  // Access state using useSelector hook
  const email = useSelector((state) => state.userPortfolio.email);
  const profile = useSelector((state) => state.userPortfolio.profile);
  const languages = useSelector((state) => state.userPortfolio.resume.languages)
    .map(lang => `${lang.language} (${lang.proficiency})`)
    .join(', ');

  // Extracting location and phone from profile if available
  const location = profile ? profile.location : 'Location not available';
  const phone = profile ? profile.phone : 'Phone not available';

  return (
    <div className="container my-4">
      <div className="row">
        <div className="col">
          <h3>User Information</h3>
          <p><strong>Location:</strong> {location}</p>
          <p><strong>Email:</strong> {email}</p>
          <p><strong>Phone:</strong> {phone}</p>
          <p><strong>Languages:</strong> {languages}</p>
        </div>
      </div>
    </div>
  );
};

export default UserInfo;
