import React, { useState } from 'react';

const RegForm = () => {
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    const handleSubmit = (event) => {
        event.preventDefault();

        if (password !== confirmPassword) {
            setErrorMessage("Passwords do not match!");
            return;
        }

        // Handle your registration logic here (e.g., API call)

        setErrorMessage(null);
    }

    return (
        <form onSubmit={handleSubmit} className="p-4 p-md-5 border rounded-3 bg-light text-dark">
            {errorMessage && (
                <div className="alert alert-danger" role="alert">
                    {errorMessage}
                </div>
            )}
            {/* Full Name Field */}
            <div className="form-floating mb-3">
                <input type="text" className="form-control" id="floatingFullName" placeholder="John Doe" />
                <label htmlFor="floatingFullName">Full Name</label>
            </div>

            {/* Email Field */}
            <div className="form-floating mb-3">
                <input type="email" className="form-control" id="floatingInput" placeholder="name@example.com" />
                <label htmlFor="floatingInput">Email address</label>
            </div>

            {/* Password Field */}
            <div className="form-floating mb-3">
                <input 
                    type="password" 
                    className="form-control" 
                    id="floatingPassword" 
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <label htmlFor="floatingPassword">Password</label>
            </div>

            {/* Confirm Password Field */}
            <div className="form-floating mb-3">
                <input 
                    type="password" 
                    className="form-control" 
                    id="floatingConfirmPassword" 
                    placeholder="Confirm Password" 
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <label htmlFor="floatingConfirmPassword">Confirm Password</label>
            </div>

            {/* Remember Me Checkbox */}
            <div className="checkbox mb-3">
                <label>
                    <input type="checkbox" value="remember-me" /> Remember me
                </label>
            </div>

            {/* Submit Button */}
            <button className="w-100 btn btn-lg btn-primary" type="submit">Sign Up for Free</button>
            <hr className="my-4" />
            <small className="text-muted">By clicking Sign up, you agree to the terms of use.</small>
        </form>
    );
}

export default RegForm;
