//  profileThunks.js 


import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const getProfile = createAsyncThunk('profile/getProfile', async (_, thunkAPI) => {
  try {
    const response = await axios.get('http://localhost:3000/api/profile', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
      },
    });
    return response.data;
  } catch (error) {
    return thunkAPI.rejectWithValue(error.response.data);
  }
});


export const updateProfile = createAsyncThunk('profile/updateProfile', async (formData, thunkAPI) => {
    try {
      const response = await axios.put('http://localhost:3000/api/profile', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("authToken")}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      return thunkAPI.rejectWithValue(error.response.data);
    }
  });
