import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { updateProfile } from '../../../reducers/authSlice'; // Assuming this is the new thunk
import { getProfile } from './profileThunks'; // Create this thunk for fetching profile

const ProfileSchema = Yup.object().shape({
  fullName: Yup.string().required('Full Name is required'),
  bio: Yup.string(),
  roleTitle: Yup.string(),
  location: Yup.string(),
  phone: Yup.string(),
  videoIntro: Yup.object().shape({
    platform: Yup.string(),
    url: Yup.string().url('Enter a valid URL'),
  }),
});

const MyProfileMain = () => {
  const dispatch = useDispatch();
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      try {
        const profileData = await dispatch(getProfile()).unwrap();
        formik.setValues(profileData);
      } catch (error) {
        console.error('Error fetching profile data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [dispatch]);

  const formik = useFormik({
    initialValues: {
      fullName: '',
      profilePicture: '',
      bio: '',
      roleTitle: '',
      location: '',
      phone: '',
      videoIntro: {
        platform: '',
        url: '',
      },
    },
    validationSchema: ProfileSchema,
    onSubmit: async (values) => {
      const formData = new FormData();
      Object.entries(values).forEach(([key, value]) => {
        if (key === 'videoIntro') {
          formData.append(key, JSON.stringify(value));
        } else {
          formData.append(key, value);
        }
      });
      if (selectedFile) {
        formData.append('profilePicture', selectedFile);
      }

      try {
        await dispatch(updateProfile(formData)).unwrap();
        // Handle success...
      } catch (error) {
        console.error('Error updating profile:', error);
        // Handle error...
      }
    },
  });

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4 bg-white text-dark">
      <h2 className="pt-3">Profile</h2>
      <form onSubmit={formik.handleSubmit}>
        {/* Full Name Input */}
        <div className="mb-3">
          <label htmlFor="fullName" className="form-label">Full Name</label>
          <input
            type="text"
            className="form-control"
            id="fullName"
            name="fullName"
            {...formik.getFieldProps('fullName')}
          />
          {formik.touched.fullName && formik.errors.fullName && (
            <div className="text-danger">{formik.errors.fullName}</div>
          )}
        </div>

        {/* Profile Picture Input */}
        <div className="mb-3">
          <label htmlFor="profilePicture" className="form-label">Profile Picture</label>
          <input
            type="file"
            className="form-control"
            id="profilePicture"
            name="profilePicture"
            onChange={handleFileChange}
          />
        </div>

        {/* Bio Input */}
        <div className="mb-3">
          <label htmlFor="bio" className="form-label">Bio</label>
          <textarea
            className="form-control"
            id="bio"
            name="bio"
            {...formik.getFieldProps('bio')}
          />
          {formik.touched.bio && formik.errors.bio && (
            <div className="text-danger">{formik.errors.bio}</div>
          )}
        </div>

        {/* Role Title Input */}
        <div className="mb-3">
          <label htmlFor="roleTitle" className="form-label">Role Title</label>
          <input
            type="text"
            className="form-control"
            id="roleTitle"
            name="roleTitle"
            {...formik.getFieldProps('roleTitle')}
          />
          {formik.touched.roleTitle && formik.errors.roleTitle && (
            <div className="text-danger">{formik.errors.roleTitle}</div>
          )}
        </div>

        {/* Location Input */}
        <div className="mb-3">
          <label htmlFor="location" className="form-label">Location</label>
          <input
            type="text"
            className="form-control"
            id="location"
            name="location"
            {...formik.getFieldProps('location')}
          />
          {formik.touched.location && formik.errors.location && (
            <div className="text-danger">{formik.errors.location}</div>
          )}
        </div>

        {/* Phone Input */}
        <div className="mb-3">
          <label htmlFor="phone" className="form-label">Phone</label>
          <input
            type="tel"
            className="form-control"
            id="phone"
            name="phone"
            {...formik.getFieldProps('phone')}
          />
          {formik.touched.phone && formik.errors.phone && (
            <div className="text-danger">{formik.errors.phone}</div>
          )}
        </div>

        {/* Video Intro Inputs */}
        <div className="mb-3">
          <label htmlFor="platform" className="form-label">Video Intro Platform</label>
          <select
            className="form-control"
            id="platform"
            name="platform"
            {...formik.getFieldProps('videoIntro.platform')}
          >
            <option value="">Select a platform</option>
            <option value="YouTube">YouTube</option>
            <option value="Vimeo">Vimeo</option>
            <option value="Dailymotion">Dailymotion</option>
            <option value="Twitch">Twitch</option>
            {/* Add more platforms as needed */}
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="url" className="form-label">Video Intro URL</label>
          <input
            type="text"
            className="form-control"
            id="url"
            name="url"
            {...formik.getFieldProps('videoIntro.url')}
          />
        </div>

        {/* Submit Button */}
        <button type="submit" className="btn btn-primary mb-3">Update Profile</button>
      </form>
    </main>
  );

};

export default MyProfileMain;
