import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { deleteEducation } from '../dashboardThunks';

const EducationList = () => {
  const dispatch = useDispatch();
  const education = useSelector(state => state.dashboard.resume.education);

  const handleDelete = (id) => {
    dispatch(deleteEducation(id));
  };

  const handleEdit = (educationEntry) => {
    // Code to open a modal/form with the educationEntry data
  };

  if (!education.length) {
    return <div>No education records found.</div>;
  }

  return (
    <div>
      {education.map(entry => (
        <div key={entry._id}>
          <h3>{entry.institution}</h3>
          <p>{entry.degree}</p>
          {/* Additional fields */}
          <button onClick={() => handleEdit(entry)}>Edit</button>
          <button onClick={() => handleDelete(entry._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default EducationList;
