import { createSlice } from '@reduxjs/toolkit';
import { fetchUserPortfolio } from '../components/UserPortfolio/userPortfolioThunks'; // Import the thunk

export const userPortfolioSlice = createSlice({
  name: 'userPortfolio',
  initialState: {
    data: null,
    loading: false,
    error: null,
  },
  reducers: {
    // Your regular reducers here
  },
  extraReducers: {
    [fetchUserPortfolio.pending]: (state) => {
      state.loading = true;
      state.error = null;
    },
    [fetchUserPortfolio.fulfilled]: (state, action) => {
      state.data = action.payload;
      state.loading = false;
    },
    [fetchUserPortfolio.rejected]: (state, action) => {
      state.error = action.payload;
      state.loading = false;
    },
  },
});

// You don't need these if you're using createAsyncThunk
// export const {
//   fetchUserPortfolioStart,
//   fetchUserPortfolioSuccess,
//   fetchUserPortfolioFailure,
// } = userPortfolioSlice.actions;

export default userPortfolioSlice.reducer;
