// UserPortfolioView.jsx
import React from 'react';
import { useSelector } from 'react-redux';
import PortfolioFooter from './PortfolioFooter';

const UserPortfolioView = () => {
  const portfolioData = useSelector((state) => state.userPortfolio.data); // Adjust the state path according to your store setup

  if (!portfolioData) {
    return <div>Loading...</div>; // or some loading spinner
  }

  // Assuming portfolioData has properties like education, experience, etc.
  return (
    <div className="portfolio-view">
      <h1>{portfolioData.profile.fullName}</h1>
      <p>{portfolioData.profile.bio}</p>
      {/* Render education section */}
      <section>
        <h2>Education</h2>
        {portfolioData.resume.education.map((edu, index) => (
          <div key={index}>
            <h3>{edu.institution}</h3>
            <p>{edu.degree} - {edu.fieldOfStudy}</p>
            <p>{edu.startDate} - {edu.endDate}</p>
          </div>
        ))}
      </section>
      {/* Render experience section */}
      <section>
        <h2>Experience</h2>
        {portfolioData.resume.workExperience.map((exp, index) => (
          <div key={index}>
            <h3>{exp.company}</h3>
            <p>{exp.title}</p>
            <p>{exp.startDate} - {exp.current ? 'Present' : exp.endDate}</p>
          </div>
        ))}
      </section>
      {/* ... other sections like projects, skills, etc. */}
      <PortfolioFooter />
    </div>
  );
};

export default UserPortfolioView;
